INSERT INTO `bugs` VALUES (1, 49145, 49145, '2005-01-24 11:03:49', '2005-01-25 10:28:37', 'L\\''ajout de bug puor les non-administrateurs n\\''est pas possible.', 'moyenne', 'bug', 'clos', 'bugs');
INSERT INTO `bugs` VALUES (2, 49145, 49145, '2005-01-24 11:04:46', '2005-01-25 10:28:49', 'Le délais du cookie n\\''est pas fixé. Quand on ferme le navigateur et qu\\''on le ré-ouvre, aucun troll n\\''est sélectionné dans la liste', 'basse', 'bug', 'clos', 'autre');
INSERT INTO `bugs` VALUES (3, 49145, 49145, '2005-01-24 13:25:37', '2005-03-22 23:38:39', 'faire une c\\''hite interface pour les admins pour voir les membres qui n\\''ont pas leur nom de blason de renseigné dans les outils\r\n\r\nEdit d\\''amoi : visibles dans les avatars / trombi', 'basse', 'souhait', 'clos', 'rg');
INSERT INTO `bugs` VALUES (4, 49145, 49145, '2005-01-24 13:42:23', '2005-01-25 14:24:28', 'Le lien pour faire le dump de la base ne semble pas fonctionner sur le serveur de prod.\r\n\r\nEDIT : corrected', 'moyenne', 'bug', 'clos', 'rg');
INSERT INTO `bugs` VALUES (5, 49145, 49145, '2005-01-24 13:45:07', '2005-01-25 10:29:03', 'La sélection de l\\''outil impacté n\\''est pas faite sur la fiche d\\''un bug. Ca tombe toujours sur le bestiaire par défaut.', 'prioritaire', 'bug', 'clos', 'bugs');
INSERT INTO `bugs` VALUES (6, 49145, 49145, '2005-01-24 13:46:25', '2005-01-25 10:29:15', 'la liste des bugs n\\''est pas bien ordonnée', 'moyenne', 'bug', 'clos', 'bugs');
INSERT INTO `bugs` VALUES (7, 49145, 28225, '2005-01-25 11:27:31', '2005-01-28 12:34:08', 'Faire un outil de générationd d\\''avatar pour Bollocks ', 'moyenne', 'souhait', 'clos', 'rg');
INSERT INTO `bugs` VALUES (8, 2690, 49145, '2005-01-25 15:17:56', '2005-03-22 23:35:15', '--Amélio de perfs--\r\n\r\nOn fait des accès inutiles au serveur quand on veut utiliser la Vue 2D.\r\nLogin, et hop! déjà une Vue est chargée. Or, (très très) souvent on se loggue pour ensuite lancer sa Vue avec d\\''autres paramètres que ceux par défaut ou bien pour lancer la Vue d\\''un autre. Bref, il y a plein d\\''accès inutiles.\r\n\r\nIl faudrait le login et arrivée sur la page de la Vue 2D.\r\nOn choisit les paramètres, et on clique sur [Lancer la Vue].\r\n\r\nCa soulagerait le bestiau.\r\n\r\nEdit d\\''amoi : non lieu cockpit ? :-)\r\n', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (9, 23454, 49145, '2005-01-26 11:55:18', '2005-03-26 10:41:24', 'Serait-il possible de réduire la taille des lignes du VTT pour améliorer la lisibilité, ainsi que de voir les lignes de légende donnant les caractéristiques des colonnes?\r\n\r\n<Commentaire Toddmiller> Effectivement ça serait pas mal de laisser au browser la possibilité d\\''élargir les colonnes s\\''il a la place... (si c\\''est possible !)\r\n\r\nEDIT d\\''amoi : corrected', 'basse', 'souhait', 'clos', 'vtt');
INSERT INTO `bugs` VALUES (10, 23454, 49145, '2005-01-26 11:58:51', '2005-01-26 12:55:38', 'Il manque le lien BESTIAIRE en haut de la page:\r\nhttp://relaismago.cat-the-psion.net/Bestiaire/\r\n\r\nEDIT : Fait', 'basse', 'souhait', 'clos', 'bestiaire');
INSERT INTO `bugs` VALUES (11, 23454, 49145, '2005-01-26 11:59:40', '2005-02-19 10:55:52', 'Il y a normalement une page avec des illustrations de Lobo (http://www.relaismago.com/guilde/la_vue.php) qui regroupe les outils. Maintenant il y a plusieurs pages et on peut demander le mot de passe plusieurs fois. Je m\\''y perds... :(', 'basse', 'souhait', 'clos', 'autre');
INSERT INTO `bugs` VALUES (12, 43246, 49145, '2005-01-26 13:50:11', '2005-03-22 23:34:32', 'mettre à jour le lien du VTT dans le menu déroulant à gauche, dans la page /guilde/index.php\r\n\r\nEdit d\\''amoi : C\\''est sur le serveur de Lobo.\r\nEdit d\\''amoi : non lieu. vtt intégrés aux outils', 'basse', 'souhait', 'clos', 'vtt');
INSERT INTO `bugs` VALUES (13, 43246, 49145, '2005-01-26 13:54:17', '2005-03-22 23:33:58', 'Un troll R&M souhaite accéder à la vue2D R&M. Le log/mdp de la guilde lui est demandé une première fois. Il clique ensuite sur http://www.relaismago.com/guilde/la_vue.php. Il arrive sur la page très jolie avec les trois portes (vue2D, guilde et bestiaire) et le vitrail (GPS). Il choisit la vue 2D. Jusque là, tout est parfait. Lorsqu’il clique sur Vue2D, une nouvelle fenêtre s’ouvre... \r\n>>  pourquoi ne pas ouvrir dans la même fenêtre ?\r\nnote: reprise d\\''une remarque du wiki\r\n\r\nEDIT d\\''amoi : c\\''est sur le serveur de Lobo.\r\nEDIT d\\''amoi : corrected : http://outils.relaismago.com', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (14, 43246, 49145, '2005-01-26 13:58:17', '2005-01-26 15:44:56', 'Numéro du troll : Popup sur un  ? tout au bout à droite (sur la même ligne !) : Numéro de trolls : Choississez dans la liste, ou tapez-le directement.\r\nbug : lorsque je choisis un nom, le numéro s’affiche dans le champ à gauche (normal) mais lorsqu’ensuite je remets le champ de droite vide, le champ de gauche affiche -1.\r\nnote: reprise du wiki (bug tjrs pas corrigé jusqu\\''à présent)\r\n\r\nEDIT d\\''amoi : ce ne sera pas corrigé.', 'basse', 'bug', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (15, 43246, 49145, '2005-01-26 14:00:07', '2005-03-22 23:32:56', '1.\r\nMot de passe.\r\nPopup sur un ? tout au bout à droite (sur la même ligne !) : C’est votre mot de passe de Mounty Hall, ou son MD5.\r\n\r\n2.\r\nMD5\r\nMD5: où est passé le lien?\r\nnote: ancienne remarque du wiki: Conserver le lien du MD5, c’est une bonne idée...mais ouvrir une nouvelle fenêtre, par contre, et PETITE, la fenêtre (pas besoin de plein écran).\r\n\r\nEDIT d\\''amoi: non lieu, une interface est rajoutée pour changer de passe', 'moyenne', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (16, 43246, 49145, '2005-01-26 14:04:20', '2005-03-22 23:12:40', 'bug : \\"Trolls à portée de tir\\", \\"Monstres dangeureux\\", \\"Ma position\\": seul le lien \\"ma position\\" fonctionne.\r\n\r\nnote: ancienne remarque du wiki\r\n\r\nEDIT d\\''amoi : C\\''est un comportement d\\''IE que l\\''on a pas avec firefox :-(', 'basse', 'bug', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (17, 43246, 49145, '2005-01-26 14:07:19', '2005-01-26 15:52:55', 'Bug Track Relais&Mago : Les Bugs ou Souhaits dans les outils\r\nVous venez de découvrir un disfonctionnement dans les outils, ou vous voulez voir une nouvelle fonctionnalité apparaître ? Cet outil est pour vous !\r\n==>> dysfonctionnement\\" s\\''écrit avec un y.', 'basse', 'souhait', 'clos', 'bugs');
INSERT INTO `bugs` VALUES (18, 43246, 49145, '2005-01-26 14:14:55', '2005-03-23 20:49:58', 'Supprimer le lien vers le Recherchator (ou l’intégrer à la page des 3 portes, demander au graphiste). A l’emplacement actuel, on a l’impression qu’il fait partie de l’outil Vue2D.\r\n\r\nnote: souhait formulé à l\\''origine ds le wiki\r\n\r\nEDIt d\\''amoi : intégré cockpit', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (19, 43246, 49145, '2005-01-26 14:17:58', '2005-03-22 23:43:52', 'Centrer sur : Remplacer par Vue centrée sur :, afficher par défaut les coordonnées de la case du centre de la vue affichée, et supprimer la petite case à cocher (devenue inutile). Pas de popup, c’est facile à comprendre.\r\n\r\namoi : Refusé. On doit utiliser le copié/collé pendant que l’on joue sa DLA. cf info [INFO IMPORTANTE]\r\n\r\nnote: remarque du wiki.\r\n\r\n==>> je persiste: pourquoi ne pas afficher les coordonnées de la case rose (= case sur laquelle la vue est centrée) par défaut? Je ne vois pas le rapport avec le rafraichissement!\r\n\r\nEdit d\\''amoi : intégré cockpit (rapport avec refresh => je ne vois plus non plus :-) )', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (20, 43246, 49145, '2005-01-26 14:21:59', '2005-03-22 23:39:51', 'Taille de la vue\r\nSupprimer le deuxième champ, on n’y comprend rien, malgré le popup actuel. Nouveau popup indispensable (sur un ?) : Par défaut, la vue sélectionnée est l’actuelle vue du troll, telle que sur MH. Cependant, vous pouvez afficher plus loin, si des trolls R&M environnants ont renseigné leur vue. Dans ce cas, vous pourrez juger de la mise à jour des cases vues en lisant en haut à droite la denière date d’actualisation.\r\n\r\nPourquoi ne pas afficher une taille de vue par défaut? 5 me paraît plus utile que 3. (et même si je ne peux pas voir plus loin, les cases non vues s’afficheront en noir, ce n’est pas grave)\r\nPourquoi ne pas offrir le même choix de limitation de la vue horizontale/verticale que dans MH ? (ça pourrait faire l’objet d’un menu dans options supplémentaires quelque part)\r\n\r\nnote: remarque du wiki, revue et corrigée.\r\n\r\nPS: le popup actuel embrouille encore plus. Le deuxième champ de vue est TOTALEMENT inutile, je le répète. Faut changer ça!\r\n\r\nEdit d\\''amoi : corrected cokpit\r\nEdit d\\''amoi : Woy, tu peux débrouiller ce souhait ?', 'moyenne', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (21, 43246, 49145, '2005-01-26 14:26:35', '2005-01-26 17:40:08', 'Zoom: Normal : popup sur un ? : Taille des cases de l’échiquier\r\nPerso : faut m’expliquer l’intérêt. Sinon, adapter la taille des cases au contenu!\r\n\r\nEDIT : c\\''est pour mettre une vue énorme et tout voir quand même... (renommé en zoom)', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (22, 43246, 49145, '2005-01-26 14:27:54', '2005-01-26 15:32:56', '1.\r\nJe peux me mettre en \\"responsable\\" lorsque je modifie la fiche d\\''un bug: c\\''est normal?\r\nEDIT : OUI\r\n2.\r\nidem pour l\\''état. \r\nEDIT : NORMAL\r\n\r\nIl n\\''y a pas trop de control dans le bug track, faut se discipliner donc un peu .... ', 'prioritaire', 'bug', 'clos', 'bugs');
INSERT INTO `bugs` VALUES (23, 43246, 49145, '2005-01-26 14:30:42', '2005-04-09 23:00:52', 'Taille en PA du troll-O-meter Si je veux seulement savoir &agrave; combien de PA je me situe de tel ou tel troll, je suis oblig&eacute; d’afficher la vue ? Pourquoi ne pas s&eacute;parer l’affichage du T-O-M de celui de la vue ?\r\n\r\nnote: remarque du wiki\r\n\r\nEdit Amoi : oui, obligation d\\''afficher la vue. Sinon, tu peux passer par le gps avec le guide de Micheline... C\\''est zolie et y-a des infos en plus !', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (24, 43246, 49145, '2005-01-26 14:33:06', '2005-03-26 11:29:29', 'Déplacer Taille en PA du troll-O-meter juste en dessous de l’image du troll-o-meter. C’est là qu’est sa place, pas en haut (à mon avis). Quitte à ce que l’utilisateur doive lancer une première fois sa vue, avant de pouvoir accéder à ce choix.\r\n\r\nnote: transfert from wiki\r\n\r\nEDIT d\\''amoi : non, les options doivent rester groupées.', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (25, 43246, 49145, '2005-01-26 14:34:10', '2005-04-09 23:19:32', 'T-O-M: possibilit&eacute; de franciser tout &ccedil;a ? \\&quot;Troll-o-m&egrave;tre\\&quot; serait plus indiqu&eacute;, &agrave; mon avis.\r\n\r\nnote: transfert from wiki\r\n\r\nEDIT Amoi : Fait \\&quot;troll-O-meter\\&quot;', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (26, 43246, 49145, '2005-01-26 14:37:07', '2005-03-22 23:17:53', 'Partie échiquier \r\n >> popups : pourquoi ne pas afficher la coordonnée de la profondeur en premier, avant le nom ? \r\n\r\nnote: transfert from wiki\r\n\r\nEdit d\\''Amoi : c\\''est un choix.', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (27, 43246, 49145, '2005-01-26 14:38:29', '2005-01-26 16:47:48', 'Partie échiquier \r\nLes GG’s : pourquoi la quantité n’est pas précisée ? Ce serait bien utile (ça peut influer les déplacements d’un troll : un petit détour pour ramasser quelques centaines de GG’s)\r\n\r\nnote: transfert from wiki\r\n\r\nEDIT d\\''amoi : c\\''est comme sur la vue. Si l\\''on voit pas que c\\''est une centaine, çà s\\''ra pas marqué... ', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (28, 43246, 49145, '2005-01-26 14:39:38', '2005-03-22 23:42:29', 'Position : x=-39 ; y=-30 ; z=-26 \r\nVue : 3 cases sur 3 (0)\r\n==>> que signifie le (0) ?\r\n\r\nEdit d\\''amoi : corrected cockpit', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (29, 43246, 49145, '2005-01-26 14:44:05', '2005-04-09 23:13:08', 'Partie Trollom&egrave;tre \r\nJe conseille de d&eacute;placer la l&eacute;gende &agrave; droite du logo \\&quot;troll-o-meter\\&quot;. \r\n\r\nnote from wiki\r\n\r\nEDIT Amoi : J\\''ai essay&eacute; et &ccedil;&agrave; le fait pas du tout. &ccedil;&agrave; prend trop de place et les legendes sont plus souvent en dessous du graph en question... Donc refus&eacute;.', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (30, 43246, 49145, '2005-01-26 14:45:18', '2005-03-22 23:24:23', 'Partie Trollomètre \r\nJe comprends rien à \r\n3 PA de distance : 1. A éclaircir. A simplifier.\r\n\r\nnote from wiki\r\n\r\nEdit d\\''amoi : non lieu cockpit', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (31, 43246, 49145, '2005-01-26 14:46:32', '2005-01-26 15:48:09', 'Ajouter le lien Ajouter un bug ou une demande d\\''amélioration à côté de => Le Bug est ajouté\r\nRetour à la liste des Bugs Retour à la fiche du Bug\r\n\r\nEDIT amoi : fait.', 'prioritaire', 'souhait', 'clos', 'bugs');
INSERT INTO `bugs` VALUES (32, 43246, 49145, '2005-01-26 14:48:16', '2005-04-09 22:56:32', 'Partie Trollom&egrave;tre \r\n  Pour l’instant, on a comme infos, par exemple ceci :\r\n\r\n0 PA de distance : 2     \r\n-30 515068 Basilisk[Nouveau] Monstre 10 \r\n-30 382963 Worg[Briscard] Monstre 7-9 \r\n\r\nOr il faudrait :\r\n\r\n1. donner les coordonn&eacute;es compl&egrave;tes X ;Y;N (plut&ocirc;t que juste -30) OU BIEN pr&eacute;ciser profondeur ou N OU ENCORE euh...non rien.\r\n2. lier les num&eacute;ros vers les &eacute;v&egrave;nements MH des monstres/trolls plut&ocirc;t que vers la vue 2D (plus utile si on met les coordonn&eacute;es exactes)\r\n3. ajouter les ent&ecirc;tes des colonnes (et en rajouter celle des PA de distance par la m&ecirc;me occasion) :\r\nPA de distance coordonn&eacute;es &eacute;v&egrave;nements nom esp&egrave;ce/races niveau \r\n2 -56 -10 -30  515068 Basilisk[Nouveau] Monstre 10 \r\n\r\nnote from wiki\r\nEdit Amoi : Fait', 'moyenne', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (33, 43246, 49145, '2005-01-26 14:51:11', '2005-04-30 17:38:03', 'Partie Trollom&egrave;tre \r\nPopup quand on clique sur une race : lier vers la description (du style : cette race l&agrave;, quand &ccedil;a vous attaque, &ccedil;a fait monter votre RM.\r\nidem pour les les liens vers les descriptifs de races de trolls MH (&ccedil;a doit bien exister quelque part). Sinon, description &agrave; cr&eacute;er.) \r\n\r\nnote from wiki\r\n\r\nEdit Amoi : Fait (et meme plus :-) )\r\n', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (34, 43246, 49145, '2005-01-26 14:53:33', '2005-03-22 23:20:59', 'Partie Trollomètre \r\nlégende :\r\n\\"Troll ami (aides-le et tu t’en feras un allié)\\" Correction de conjugaison : \\"Troll ami (aide-le et tu t’en feras un allié)\\"\r\n\r\nnote from wiki\r\nEdit d\\''Amoi : corrected', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (35, 43246, 49145, '2005-01-26 14:54:49', '2005-03-22 23:20:08', 'Partie Trollomètre \r\nLa distance en PA suppose l’utilisation de la compétence Déplacement Eclair, ok. Mais tient-elle compte de l\\''occupation des cases ou pas ? Si oui, à préciser quelque part (bulle) (sinon aussi!).\r\n\r\nEDIT : Amoi. Non, l\\''occupation n\\''est pas prise en compte. Ce s\\''ra rajouté dans l\\''aide future du cockpit', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (36, 43246, 49145, '2005-01-26 14:56:24', '2005-01-26 16:46:15', 'Divers \r\n>>>  lien Retour là haut  : très bien. à conserver. \r\n>>>  TOUT ce qu’il y a en dessous : à virer. Ou bien faut m’expliquer l’utilité pour l’utilisateur lambda. \r\n>>>  si on décide de conserver ce qu’il y a en bas, il faudrait ajouter une ligne de séparation avant.\r\n\r\nnote from wiki\r\n\r\nEdit d\\''amoi : on conserve, c\\''est pour les développeurs...', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (37, 43246, 28225, '2005-01-26 14:59:13', '2005-01-28 12:41:17', 'Possibilité d’avoir un menu contextuel lorsque l’on clique droit sur la vue2D ? Ca allègerait fortement tous les critères, et ça remettrait même en question toute l’interface, je pense...bref, si oui, alors il y aura de gros changements en perspective.\r\n\r\nEDIT WOY:\r\nImpossible à faire. Il fadurait du flash ou du Jaja. Et là, les soucis IE / Firefox.. c\\''est même pas la peine... !', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (38, 43246, 43246, '2005-01-26 15:07:53', '2005-03-22 23:20:26', 'Corriger les fautes de français des popups. \r\nAmoi : arf :-) faut que je relise… ;-) ?!\r\n>>> copie/colle sous word, ça te corrigera tes fautes...\r\n\r\nEDIT d\\''amoi : hum... Je peux avoir un patch / correction ? ', 'basse', 'souhait', 'clos', 'gps');
INSERT INTO `bugs` VALUES (39, 43246, 49145, '2005-01-26 15:10:23', '2005-03-22 23:26:03', 'toutes les popups d\\''aide du guide de Micheline affichent la même chose. Se contenter d\\''UNE SEULE popup à côté du titre Guide de Micheline.\r\n\r\nnote from wiki\r\n\r\nEdit d\\''amoi : choix', 'basse', 'souhait', 'clos', 'gps');
INSERT INTO `bugs` VALUES (40, 43246, 49145, '2005-01-26 15:11:43', '2005-03-26 09:26:17', 'Baronniespopup d\\''aide: Rajouter un point à la fin de la 2e phrase. \r\n\r\nnote from wiki\r\n\r\nEDIT d\\''amoi : corrected', 'basse', 'souhait', 'clos', 'gps');
INSERT INTO `bugs` VALUES (41, 43246, 49145, '2005-01-26 15:13:02', '2005-03-22 23:36:21', 'Remplacer ENGINE par RG partout, dans tous les outils.\r\nExemple: GPSadv / PI / Tanières RM / popup d\\''aide.\r\n\r\nEdit d\\''amoi : dès que je le vois, je le corrige systématiquement', 'basse', 'souhait', 'clos', 'autre');
INSERT INTO `bugs` VALUES (42, 43246, 49145, '2005-01-26 15:15:48', '2005-03-22 23:41:54', 'RECHERCHATOR:\r\nOffrir un choix (menu déroulant) pour la race (et changer le menu en fonction du choix de départ: troll ou monstre). Taper à la main, c\\''est pas super.\r\n\r\nEdit d\\''amoi : corrected cockpit', 'basse', 'souhait', 'clos', 'recherchator');
INSERT INTO `bugs` VALUES (43, 43246, 49145, '2005-01-26 15:18:00', '2005-03-22 23:44:33', 'RECHERCHATOR\r\nordre de tri: \\"Z\\". Je ne comprends pas. Ce serait pas plutôt \\"N\\" ?\r\n\r\nEdit d\\''amoi : Corrected cockpit', 'basse', 'souhait', 'clos', 'recherchator');
INSERT INTO `bugs` VALUES (44, 43246, 49145, '2005-01-26 15:21:52', '2005-03-22 23:12:07', 'Recherchator\r\nJe recherche un troll dont je renseigne la race (j\\''écris windaube). Lorsque je valide, les trois champs du pédigree se remplissent avec la même information : windaube. \r\n\r\nnote from wiki\r\nEDIT : non lieu avec le cockpit', 'moyenne', 'bug', 'clos', 'recherchator');
INSERT INTO `bugs` VALUES (45, 43246, 49145, '2005-01-26 15:27:48', '2005-01-26 15:31:46', 'c\\''est quoi l\\''outil \\"ggc\\" ?\r\n\r\nEDIT : c\\''est l\\''outil de gestion de groupe de chasses en cours de dev', 'basse', 'bug', 'clos', 'ggc');
INSERT INTO `bugs` VALUES (46, 43246, 49145, '2005-01-26 15:30:38', '2005-03-22 23:32:07', 'fiches trolls: améliorer la lisibilité des caractéristiques, par exemple en mettant en gras les titres (MM, RM, kills etc.)\r\n\r\nEdit d\\''amoi : à priori non, après le passage de la css officiel, c\\''est plus clair.', 'moyenne', 'souhait', 'clos', 'rg');
INSERT INTO `bugs` VALUES (47, 43246, 49145, '2005-01-26 17:48:23', '2005-02-02 00:12:26', 'utilité de la page http://relaismago.cat-the-psion.net/vue2d/statistiques.php ?\r\nJe propose sa suppression...puisque le GPS fait la même chose, finalement.\r\n\r\nEDIT amoi : non, c\\''est pas exactement pareil. Avec les stats, c\\''et une vue de grand ensemble... le gps, c\\''est un outil de précision ;-)  On Garde.', 'basse', 'souhait', 'clos', 'stats');
INSERT INTO `bugs` VALUES (48, 43246, 49145, '2005-01-26 17:59:04', '2005-04-27 10:54:38', 'bestiaire: appliquer la m&ecirc;me mise en forme pour les liens du haut et la couleur g&eacute;n&eacute;rale, que les autres pages (vtt bugs vue2D etc.).\r\n\r\nEDIT Amoi : Non Lieu nouveau Bestiaire', 'basse', 'souhait', 'clos', 'bestiaire');
INSERT INTO `bugs` VALUES (49, 43246, 49145, '2005-01-26 18:18:29', '2005-03-22 23:25:06', 'RECHERCHATOR  résultats: remplacer POS par POSITION. et espacer un peu plus les colonnes.\r\n\r\nEdit d\\''amoi : non lieu cockpit', 'basse', 'souhait', 'clos', 'recherchator');
INSERT INTO `bugs` VALUES (50, 43246, 49145, '2005-01-26 18:25:17', '2005-03-22 23:37:36', 'Souhait.\r\nUn truc que tout le monde demande depuis super longtemps, un générateur de texte en police R&M pour pouvoir insérer des images dans nos profils MH. Ce serait possible d\\''améliorer celui existant? cf http://www.irminger.ch/generateur/ \r\nCe qui manque, c\\''est le contour des lettres, comme sait le faire je-sais-plus-qui du topic blasons ou personnaliser son profil MH du forum... Et puis, il faudrait pouvoir alléger l\\''image générée (je ne peux la sauvegarder qu\\''en bmp, chez moi).\r\n\r\nEdit d\\''amoi : demande d\\''un nouvel d\\''outil. Un dev le fait s\\''il souhaite... ', 'basse', 'souhait', 'clos', 'autre');
INSERT INTO `bugs` VALUES (51, 31202, 28225, '2005-01-26 20:01:35', '2005-01-28 12:35:23', 'Les noms des guildes dans la liste des RG restent avec les caracteres speciaux HTML...\r\n\r\nC\\''était dans la page des ambassadeurs', 'moyenne', 'bug', 'clos', 'rg');
INSERT INTO `bugs` VALUES (52, 10611, 10611, '2005-01-26 22:36:44', '2005-01-27 20:44:16', 'Lors de la connexion à la vue2D (http://relaismago.cat-the-psion.net/vue2d/) il me demande mon login et mp. Ensuite il lance la page : http://relaismago.cat-the-psion.net/vue2d/inc_authent.php3 et reste sur cette page. Je suis obligé de faire précédent pour arriver à ma vue\r\n\r\nEDIT d\\''amoi : ajout d\\''un lien si le redirect fonctionne pas.\r\nEDIT d\\''amoi : peux-tu confirmer que çà marche ?\r\nEDIT de Fuleng : Je clos ça fonctionne bien à présent !', 'basse', 'bug', 'clos', 'autre');
INSERT INTO `bugs` VALUES (53, 10611, 49145, '2005-01-26 22:38:18', '2005-03-22 23:19:11', 'Lorsque on suit les évênenements des trolls qui entourent le notre dans le hall et que l\\''un d\\''entre eux meurt. Il apparait comme \\''disparu\\'' sur la vue2D alors que l\\''on sait qu\\''il est mort et donc plus là. \r\nY aurait-il un moyen de faire une annonce comme quoi il est mort et donc plus du tout là ? (pour avoir une vue bien à jour)\r\nOu alors il faudrait régulièrement \\''nettoyer\\'' la base de la vue pour ne laisser apparaitre un troll qu\\''une seule fois dans tout le hall (dans le cas du mort il apparait 1 fois comme disparu à son ancien emplacement et une fois au nouveau).\r\n\r\n\r\nEDIT d\\''Amoi : si le troll est vu autre part, le fantôme n\\''existe plus. Mais c\\''est vrai que ce s\\''rait pas mal d\\''avoir un bouton pour dire qu\\''il est mort et que l\\''on ne connait pas ses coordonnées...\r\n=> ce s\\''ra sans doute pas fait. C\\''est bien d\\''avoir cette information  ', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (54, 43246, 49145, '2005-01-27 17:15:43', '2005-03-23 20:46:29', 'Ce serait possible de récupérer l\\''outil ci-dessous, très pratique?\r\nhttp://www.fyma.ucl.ac.be/~goffinet/cyclotrolls/monsterdla.php\r\nCa pourrait être intégré au bestiaire, non?\r\nEDIT kelu: et/ou intégré au futur GCC, pour connaître les heures les plus propices pour les hypnos/insultes !\r\n\r\nEDIT d\\''amoi : Nouvel outil. Si un dev veut l\\''intégrer, pourquoi pas. Je close le bug, mais retients l\\''url dans mes bookmarks...\r\nLes sources n\\''ont pas l\\''air d\\''être dispo en plus :-(', 'moyenne', 'souhait', 'clos', 'bestiaire');
INSERT INTO `bugs` VALUES (55, 43246, 49145, '2005-01-27 17:35:31', '2005-04-08 13:20:16', 'EDIT kelu: ici m&ecirc;me dans ce champ de description des bugs.\r\nEDIT Woy o&ugrave; ?\r\n\r\nLes antislashs n\\''apparaissent pas ici.\r\nEDIT amoi : Exact, ce ne sera pas corrig&eacute;.\r\n\r\n', 'basse', 'bug', 'clos', 'bugs');
INSERT INTO `bugs` VALUES (56, 51166, 49145, '2005-01-27 22:44:46', '2005-04-21 09:28:36', 'Ce serait cool de pouvoir choisir de voir la vue par niveau (comme par exemple la vue des bricol\\''trolls). Comme &ccedil;a on pourrait voir d\\''un seul coup d\\''oeil s\\''il y a quelqu\\''un ou quelquechose au dessus ou au dessous de nous.\r\n\r\nEdit Amoi : fait (grouper les niveau z :  oui / non)', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (57, 51166, 49145, '2005-01-27 22:52:25', '2005-04-21 09:28:12', 'ce serait bien d\\''avoir la possibilit&eacute; de ne pas afficher les trolls disparus (parfois &ccedil;a encombre pas mal la vue...)\r\n\r\n[EDIT Toddmiller] ...ou bien, avoir la possibilit&eacute; de marquer avant l\\''heure des trolls comme \\&quot;n\\''habite vraiment plus &agrave; l\\''adresse indiqu&eacute;e\\&quot;, quand on SAIT qu\\''ils sont morts et non pas planqu&eacute;s dans un coin\r\n\r\nEDIT Amoi : Fait : On peut cacher les disparus...', 'basse', 'souhait', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (58, 51166, 49145, '2005-01-30 13:35:08', '2005-02-19 10:55:05', 'Quand j\\''augmente la taille de ma vue, dans les cases en dehors de ma vue normale, rien n\\''apparait !\r\nSinon j\\''ai rien compris à l\\''histoire du +- ...\r\n[edit]glupglup le 19/02/05 : ok j\\''ai compris l\\''histoire du +- (il aura fallu le temps ;)) mais ça marche pas non plus...', 'moyenne', 'bug', 'clos', 'vue2d');
INSERT INTO `bugs` VALUES (59, 43246, 10611, '2005-01-30 23:17:27', '2005-02-02 19:52:06', 'Lorsque l\\''on fait la MàJ de son profil: \\"Mise à jour résussie !\\" ==>> faute d\\''orthographe: \\"réussie\\"\r\n\r\n[EDIT] Fuleng : Corrigé, reste à mettre en prod', 'basse', 'bug', 'clos', 'ggc');
INSERT INTO `bugs` VALUES (60, 43246, 10611, '2005-01-30 23:19:08', '2005-04-08 23:54:09', 'ajout d\\''un monstre: pourquoi les champs \\&quot;Race\\&quot;, \\&quot;Monstre\\&quot; et \\&quot;Template\\&quot; sont s&eacute;par&eacute;s? On peut pas faire un copier/coller du nom du monstre, directement?\r\n\r\n[EDIT] Fuleng : &ccedil;a va venir ^^ Il y aura le m&ecirc;me parser que celui du bestaire ... ce sera plus simple ! \r\n[EDIT] Fuleng : ces champs vont disparaitre. Le bestiaire est en cours de refonte donc on verra quand il sera refondu. Mise &agrave; jour d\\''ici quelques jours.', 'basse', 'souhait', 'clos', 'ggc');
INSERT INTO `bugs` VALUES (61, 43246, 10611, '2005-01-30 23:26:30', '2005-02-02 19:51:29', 'lors de l\\''ajout d\\''un évènement d\\''un monstre... 2 fautes: \\"Ajout réussit ! L\\''évênement est ajouté !\\" ==>> \\"Ajout réussi! L\\''évènement est ajouté !\\"\r\n\r\n[EDIT] Fuleng : Corrigé pour la prochaine mise en prod', 'basse', 'bug', 'clos', 'ggc');
INSERT INTO `bugs` VALUES (62, 36452, 10611, '2005-01-31 21:03:16', '2005-02-02 19:49:59', 'Les noms de trolls comportant un \\'' (comme Teal\\''k) ne fonctionnent pas avec le parser.\r\n\r\nDonc CC impossible sans édition du nom.\r\n[EDIT] Fuleng : J\\''ai corrigé .. un addslashes qui manquait cruellement. Correction effective à la prochaine livraison.', 'basse', 'bug', 'clos', 'ggc');
INSERT INTO `bugs` VALUES (63, 51166, 49145, '2005-01-31 23:23:09', '2005-02-02 00:06:55', 'Il y a deux liens vers le bestiaire sur le menu\r\n\r\nEDIT amoi : corrected', 'basse', 'bug', 'clos', 'autre');
INSERT INTO `bugs` VALUES (64, 43246, 10611, '2005-02-01 07:08:01', '2005-02-12 20:43:34', 'SOUHAIT:\r\nLorsque l\\''on met à jour notre profil dans le GGC, que cela fasse double emploi avec le VTT...automatiquement.\r\n\r\nEdit d\\''amoi : Fuleng, tu prends en charge ? y-a les fonctions de maj de bdd (vtt) dans admin_functions.php et admin_functions_db.php. Attention, c\\''est uniquement valabale si le troll qui met sa vue à jour est RelaisMago (if $_SESSION[AuthGuilde] == 450 ) => s\\''il est authentifié avec les outils...\r\n\r\n[EDIT] Fuleng : euh ba je vais voir ça ^^\r\n\r\n[EDIT] Fuleng : c\\''est fait !', 'basse', 'souhait', 'clos', 'ggc');
INSERT INTO `bugs` VALUES (65, 51166, 49145, '2005-02-01 23:27:34', '2005-02-02 00:04:55', 'sur la vue publique, j\\''ai parsé ma vue (copié collé) et au lieu d\\''avoir une vue sur 3 cases, j\\''avais une vue sur 4 cases et le plus drôle c qu\\''il y avait des infos sur les cases que je devais pas voire normalement (alors que ça ça marche pas sur la vue2d privée) ...\r\n\r\nEDIT amoi : quand on est encore connecté sur les outils, et que l\\''on utilise la vue publique, c\\''est sûr qu\\''il y a des soucis... Pas bug donc.', 'moyenne', 'bug', 'clos', 'vue2d');
