INSERT INTO `bugs` VALUES (170, 4570, 0, '2005-07-08 08:32:38', '0000-00-00 00:00:00', 'L\\''outil des compos prios ne fonctionne pas du tout ! :-(\r\n\r\nQuand on veut modifier un compo ou en rajouter un, la liste dans laquelle on est cens&eacute; trouver la race du monstre est vide. Le seul truc qu\\''on puisse renseigner c\\''est le Nom, qui ne sert a priori &agrave; rien... Du coup aucun des derniers monstres renseign&eacute;s (Barghest, Chim&egrave;re, etc.) n\\''appara&icirc;t comme prioritaire dans la Vue 2D.\r\n\r\nDe plus, il y a 6 niveaux de priorit&eacute; possibles, alors que dans la Vue 2D il n\\''y en a que 3 qui sont repr&eacute;sent&eacute;s (en comptant \\&quot;neutre\\&quot;). Comment sait-on ce qu\\''on doit mettre dans l\\''IHM pour que les monstres apparaissent sur fond orange, ou sur fond rouge, dans la Vue 2D ?', 'prioritaire', 'bug', 'ouvert', 'rg');
INSERT INTO `bugs` VALUES (171, 4570, 0, '2005-07-08 09:14:39', '0000-00-00 00:00:00', 'Niveau du Dindon Du Glacier : une &eacute;tude EM de Grognon a montr&eacute; qu\\''il n\\''&eacute;tait pas niveau 1, mais -4.', 'moyenne', 'bug', 'ouvert', 'bestiaire');
